
var date=new Date,notice=document.querySelector(".site-notice"),dismissButton=document.querySelector(".site-notice-dismiss"),actionButton=document.querySelector(".action-btn");if(null!==notice){if(notice.hasAttribute("data-cookie-name"))var cookieName=notice.getAttribute("data-cookie-name");if(notice.hasAttribute("data-cookie-value"))var cookieValue=notice.getAttribute("data-cookie-value");[dismissButton,actionButton].forEach(e=>{e.addEventListener("click",function(){date.setTime(date.getTime()+31536e6),document.cookie=cookieName+"="+cookieValue+";expires="+date.toUTCString()+";path=/",notice.style.opacity="0",setTimeout(function(){notice.parentNode.removeChild(notice)},500)})})}
